import React, { useState, useEffect } from "react";
import Button from "./components/ui/Button";
import Card from "./components/ui/Card";
import CardContent from "./components/ui/CardContent";
import Input from "./components/ui/Input";
import Navbar from "./components/ui/NavBar";
import DrivingLicenseCard from "./components/templates/DrivingLicenseCard";

const DigitalIdentityApp = () => {
  const [identity, setIdentity] = useState(null);
  const [walletAddress, setWalletAddress] = useState(null);
  const [userData, setUserData] = useState({
    name: "",
    surname: "",
    fathersName: "",
    mothersName: "",
    dateOfBirth: "",
    placeOfBirth: "",
    issuanceOffice: "",
    idNumber: "",
    datePublish: "",
  });
  const [showLicenseCard, setShowLicenseCard] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [showWarning, setShowWarning] = useState(false);
  const [uploadedImage, setUploadedImage] = useState(null);

  // Reset walletAddress on page reload
  useEffect(() => {
    setWalletAddress(null);
  }, []);

  // Handle unmount or page close event to disconnect the wallet
  useEffect(() => {
    const handleBeforeUnload = () => {
      if (window.keplr) {
        window.keplr.disable();
        console.log("Wallet disconnected");
      }
    };

    window.addEventListener("beforeunload", handleBeforeUnload);
    return () => {
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const allowedTypes = ["image/png", "image/jpeg"];
      if (allowedTypes.includes(file.type)) {
        setUploadedImage(file);
        console.log("Image uploaded:", file.name);
      } else {
        alert("Invalid file type. Please upload a PNG or JPEG image.");
      }
    }
  };

  const handleGenerateIdentity = () => {
    // Check if at least one field has been changed
    const isAnyFieldChanged = Object.values(userData).some((value) => value !== "");

    if (!isAnyFieldChanged) {
      setShowWarning(true); // Show warning message
      setTimeout(() => setShowWarning(false), 3000); // Hide warning after 3 seconds
      return;
    }

    // If at least one field is filled, proceed to generate the identity
    setIdentity({
      ...userData,
      blockchainProof: "0x123abc456def",
      uploadedImage: uploadedImage ? URL.createObjectURL(uploadedImage) : null,
    });
  };

  const handleClickPlus = () => {
    setIsDropdownOpen((prev) => !prev);
  };

  const handleInsertLicense = () => {
    setShowLicenseCard(!showLicenseCard);
    setIsDropdownOpen(false);
  };

  const handleGenerateLicense = (licenseData) => {
    console.log("Generated License:", licenseData);
  };

  const handleConnectWallet = async () => {
    if (window.keplr) {
      try {
        await window.keplr.enable("nillion-chain-testnet-1");

        const offlineSigner = window.keplr.getOfflineSigner("nillion-chain-testnet-1");
        const accounts = await offlineSigner.getAccounts();

        setWalletAddress(accounts[0].address);
      } catch (error) {
        console.error("Failed to connect wallet:", error);
      }
    } else {
      alert("Please install Keplr Wallet!");
    }
  };

  const handleInsertSmallPaper = () => {
    console.log("Insert Small Paper functionality");
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Navbar */}
      <Navbar
        walletAddress={walletAddress}
        onConnectWallet={handleConnectWallet}
        onInsertSmallPaper={handleInsertSmallPaper}
      />

      <div
        className="flex flex-col items-center justify-center flex-grow p-6"
        style={{
          backgroundImage: "url(/nillion-wave-img.jpg)",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        {/* "+" Icon and Connect Wallet Button */}
        <div className="absolute top-16 right-6 z-10 flex space-x-4">
          <div className="text-4xl cursor-pointer plus-icon" onClick={handleClickPlus}>
            +
          </div>

          {isDropdownOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200">
              <div className="p-3 hover:bg-gray-100 cursor-pointer" onClick={handleInsertLicense}>
                Insert License
              </div>
            </div>
          )}
        </div>

        {/* Display Connected Wallet Address */}
        {walletAddress && (
          <div className="mt-4 p-3 bg-green-100 border border-green-400 rounded-lg shadow-md text-gray-900">
            <strong>Connected Wallet:</strong> <span className="text-green-700">{walletAddress}</span>
          </div>
        )}

        {/* Main Card */}
        <Card className="w-full max-w-5xl p-8 bg-white rounded-3xl shadow-2xl flex flex-col items-center">
          <CardContent>
            <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">
              Blockchain Digital Identity
            </h2>

            <form className="grid grid-cols-3 gap-6 w-full">
              <Input name="idNumber" placeholder="Enter ID Number" value={userData.idNumber} onChange={handleInputChange} className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1" />
              <Input name="name" placeholder="Enter Name" value={userData.name} onChange={handleInputChange} className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1" />
              <Input name="surname" placeholder="Enter Surname" value={userData.surname} onChange={handleInputChange} className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1" />
              <Input name="fathersName" placeholder="Enter Father's Name" value={userData.fathersName} onChange={handleInputChange} className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1" />
              <Input name="mothersName" placeholder="Enter Mother's Name" value={userData.mothersName} onChange={handleInputChange} className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1" />
              <Input name="dateOfBirth" type="date" placeholder="Enter Date of Birth" value={userData.dateOfBirth} onChange={handleInputChange} className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1" />
              <Input name="placeOfBirth" placeholder="Enter Place of Birth" value={userData.placeOfBirth} onChange={handleInputChange} className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1" />
              <Input name="issuanceOffice" placeholder="Enter Issuance Office" value={userData.issuanceOffice} onChange={handleInputChange} className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1" />
              <Input name="datePublish" type="date" placeholder="Enter Date of Publish" value={userData.datePublish} onChange={handleInputChange} className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1" />
            </form>

            {/* Warning Message */}
            {showWarning && (
              <div className="mt-4 p-3 bg-yellow-100 border border-yellow-400 text-yellow-700 rounded-lg shadow-md">
                Please fill in at least one field before generating the identity.
              </div>
            )}

            <Button type="button" onClick={handleGenerateIdentity} className="mt-6 w-full bg-blue-700 text-white py-3 rounded-lg shadow-md hover:bg-[#4159F6] transition transform hover:scale-105">
              Generate Identity
            </Button>

            {identity && (
              <div className="mt-6 p-6 bg-gray-100 border border-gray-200 rounded-lg shadow-md w-full grid grid-cols-3 gap-4 text-left">
                <p className="text-lg font-medium text-gray-800"><strong>ID Number:</strong> {identity.idNumber}</p>
                <p className="text-lg font-medium text-gray-800"><strong>Name:</strong> {identity.name}</p>
                <p className="text-lg font-medium text-gray-800"><strong>Surname:</strong> {identity.surname}</p>
                <p className="text-lg font-medium text-gray-800"><strong>Father's Name:</strong> {identity.fathersName}</p>
                <p className="text-lg font-medium text-gray-800"><strong>Mother's Name:</strong> {identity.mothersName}</p>
                <p className="text-lg font-medium text-gray-800"><strong>Date of Birth:</strong> {identity.dateOfBirth}</p>
                <p className="text-lg font-medium text-gray-800"><strong>Place of Birth:</strong> {identity.placeOfBirth}</p>
                <p className="text-lg font-medium text-gray-800"><strong>Issuance Office:</strong> {identity.issuanceOffice}</p>
                <p className="text-lg font-medium text-gray-800"><strong>Date of Publish:</strong> {identity.datePublish}</p>
                <p className="text-lg font-medium text-gray-800 col-span-3"><strong>Blockchain Proof:</strong> <span className="text-green-600 font-mono">{identity.blockchainProof}</span></p>
                {identity.uploadedImage && (
                  <div className="col-span-3">
                    <p className="text-lg font-medium text-gray-800"><strong>Uploaded Image:</strong></p>
                    <img
                      src={identity.uploadedImage}
                      alt="Uploaded Identity Image"
                      className="max-w-full h-auto rounded-lg shadow-md"
                    />
                  </div>
                )}
              </div>
            )}
            
          </CardContent>

            {/* Image Upload Section */}
            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Upload Identity Image (PNG, JPEG)
              </label>
              <input
                type="file"
                accept=".png, .jpeg, .jpg"
                onChange={handleImageUpload}
                className="block w-full text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50 focus:outline-none"
              />
              {uploadedImage && (
                <p className="mt-2 text-sm text-gray-500">
                  Uploaded: {uploadedImage.name}
                </p>
              )}
            </div>

            {/* Display Uploaded Image */}
            {uploadedImage && (
              <div className="mt-4">
                <img
                  src={URL.createObjectURL(uploadedImage)}
                  alt="Uploaded Identity Image"
                  className="max-w-full h-auto rounded-lg shadow-md"
                />
              </div>
            )}
          <CardContent>

          
          </CardContent>
        </Card>

        {showLicenseCard && (
          <div className="mt-8 w-full max-w-5xl">
            <DrivingLicenseCard onGenerateLicense={handleGenerateLicense} />
          </div>
        )}
      </div>

      {/* Footer */}
      <div className="bg-gray-800 text-white text-center py-4">
        <p>© 2025 All Rights Reserved <a>GitHub</a></p>
      </div>
    </div>
  );
};

export default DigitalIdentityApp;
