import React, { useState, useEffect } from "react";
import Button from "./components/ui/Button";
import Card from "./components/ui/Card";
import CardContent from "./components/ui/CardContent";
import Input from "./components/ui/Input";
import DrivingLicenseCard from "./components/templates/DrivingLicenseCard";

// Navbar component
const Navbar = ({ walletAddress, onConnectWallet, onInsertSmallPaper }) => (
  <div className="bg-gray-800 text-white w-full py-4 px-6 fixed top-0 left-0 z-20">
    <div className="flex justify-between items-center">
      <div className="text-xl font-bold">Digital Identity</div>
      <div className="flex items-center space-x-6">
        <button
          onClick={onInsertSmallPaper}
          className="text-white hover:text-gray-300 transition"
        >
          SmallPaper
        </button>
        <Button
          type="button"
          onClick={onConnectWallet}
          className="bg-gray-700 text-white py-2 px-4 rounded-lg shadow-md hover:bg-blue-600 transition"
        >
          {walletAddress ? "Wallet Connected" : "Connect Wallet"}
        </Button>
      </div>
    </div>
  </div>
);

// DigitalIdentityApp component
const DigitalIdentityApp = () => {
  const [identity, setIdentity] = useState(null);
  const [walletAddress, setWalletAddress] = useState(null);
  const [userData, setUserData] = useState({
    name: "",
    surname: "",
    fathersName: "",
    mothersName: "",
    dateOfBirth: "",
    placeOfBirth: "",
    issuanceOffice: "",
    idNumber: "",
    datePublish: "",
  });
  const [showLicenseCard, setShowLicenseCard] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  // Reset walletAddress on page reload
  useEffect(() => {
    setWalletAddress(null); // Clear wallet address on page refresh
  }, []);

  // Handle unmount or page close event to disconnect the wallet
  useEffect(() => {
    const handleBeforeUnload = () => {
      if (window.keplr) {
        window.keplr.disable();
        console.log("Wallet disconnected");
      }
    };
    window.addEventListener("beforeunload", handleBeforeUnload);
    return () => window.removeEventListener("beforeunload", handleBeforeUnload);
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle identity generation
  const handleGenerateIdentity = () => {
    setIdentity({
      ...userData,
      blockchainProof: "0x123abc456def", // Simulated blockchain proof
    });
  };

  // Toggle dropdown visibility
  const handleClickPlus = () => setIsDropdownOpen((prev) => !prev);

  // Insert license card toggle
  const handleInsertLicense = () => {
    setShowLicenseCard(!showLicenseCard);
    setIsDropdownOpen(false);
  };

  // Generate License action
  const handleGenerateLicense = (licenseData) => {
    console.log("Generated License:", licenseData);
  };

  // Connect wallet function
  const handleConnectWallet = async () => {
    if (window.keplr) {
      try {
        await window.keplr.enable("nillion-chain-testnet-1");
        const offlineSigner = window.keplr.getOfflineSigner("nillion-chain-testnet-1");
        const accounts = await offlineSigner.getAccounts();
        setWalletAddress(accounts[0].address);
      } catch (error) {
        console.error("Failed to connect wallet:", error);
        alert("Failed to connect wallet. Please try again.");
      }
    } else {
      alert("Please install Keplr Wallet!");
    }
  };

  // Insert Small Paper functionality placeholder
  const handleInsertSmallPaper = () => {
    console.log("Insert Small Paper functionality");
    // Implement SmallPaper functionality here
  };

  return (
    <div className="min-h-screen pt-24 bg-gray-100">
      <Navbar
        walletAddress={walletAddress}
        onConnectWallet={handleConnectWallet}
        onInsertSmallPaper={handleInsertSmallPaper}
      />

      <div
        className="flex flex-col items-center justify-center min-h-screen p-6"
        style={{
          backgroundImage: "url(/nillion-wave-img.jpg)",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <div className="absolute top-16 right-6 z-10 flex space-x-4">
          <div
            className="text-4xl text-gray-800 cursor-pointer"
            onClick={handleClickPlus}
          >
            +
          </div>
          <Button
            type="button"
            onClick={handleConnectWallet}
            className="w-36 bg-gray-800 text-white py-2 rounded-lg shadow-md hover:bg-blue-700 transition transform hover:scale-105"
          >
            {walletAddress ? "Wallet Connected" : "Connect Wallet"}
          </Button>

          {isDropdownOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200">
              <div
                className="p-3 hover:bg-gray-100 cursor-pointer"
                onClick={handleInsertLicense}
              >
                Insert License
              </div>
            </div>
          )}
        </div>

        {walletAddress && (
          <div className="mt-4 p-3 bg-green-100 border border-green-400 rounded-lg shadow-md text-gray-900">
            <strong>Connected Wallet:</strong>{" "}
            <span className="text-green-700">{walletAddress}</span>
          </div>
        )}

        <Card className="w-full max-w-5xl p-8 bg-white rounded-3xl shadow-2xl flex flex-col items-center">
          <CardContent>
            <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">
              Blockchain Digital Identity
            </h2>

            <form className="grid grid-cols-3 gap-6 w-full">
              {["idNumber", "name", "surname", "fathersName", "mothersName", "dateOfBirth", "placeOfBirth", "issuanceOffice", "datePublish"].map((field, idx) => (
                <Input
                  key={idx}
                  name={field}
                  placeholder={`Enter ${field.replace(/([A-Z])/g, " $1")}`}
                  value={userData[field]}
                  onChange={handleInputChange}
                  type={field.includes("Date") ? "date" : "text"}
                />
              ))}
            </form>

            <Button
              type="button"
              onClick={handleGenerateIdentity}
              className="mt-6 w-full bg-gray-800 text-white py-3 rounded-lg shadow-md hover:bg-blue-700 transition transform hover:scale-105"
            >
              Generate Identity
            </Button>

            {identity && (
              <div className="mt-6 p-6 bg-gray-100 border border-gray-200 rounded-lg shadow-md w-full grid grid-cols-3 gap-4 text-left">
                {Object.entries(identity).map(([key, value], idx) => (
                  <p key={idx} className="text-lg font-medium text-gray-800">
                    <strong>{key.replace(/([A-Z])/g, " $1")}:</strong> {value}
                  </p>
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        {showLicenseCard && (
          <div className="mt-8 w-full max-w-5xl">
            <DrivingLicenseCard onGenerateLicense={handleGenerateLicense} />
          </div>
        )}
      </div>

      <footer className="absolute bottom-0 w-full bg-gray-800 text-white text-center py-4">
        <p>Â© 2025 All Rights Reserved</p>
      </footer>
    </div>
  );
};

export default DigitalIdentityApp;
