// src/components/ui/DigitalIdentityApp.js
import React, { useState } from "react";
import Button from "./components/ui/Button";
import Card from "./components/ui/Card";
import CardContent from "./components/ui/CardContent";
import Input from "./components/ui/Input";

const DigitalIdentityApp = () => {
  const [identity, setIdentity] = useState(null);
  const [userData, setUserData] = useState({ name: "", id: "" });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleGenerateIdentity = () => {
    setIdentity({
      ...userData,
      blockchainProof: "0x123abc456def",
    });
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-r from-blue-100 to-purple-200 p-6">
      <Card className="w-full max-w-lg p-8 bg-white rounded-3xl shadow-2xl">
        <CardContent>
          <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">
            Blockchain Digital Identity
          </h2>

          <form className="space-y-6">
            <Input
              name="name"
              placeholder="Enter Name"
              value={userData.name}
              onChange={handleInputChange}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-400"
            />
            <Input
              name="id"
              placeholder="Enter ID Number"
              value={userData.id}
              onChange={handleInputChange}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-400"
            />
            <Button
              type="button"
              onClick={handleGenerateIdentity}
              className="w-full bg-blue-600 text-white py-3 rounded-lg shadow-md hover:bg-blue-700 transition transform hover:scale-105"
            >
              Generate Identity
            </Button>
          </form>

          {identity && (
            <div className="mt-6 p-6 bg-gray-100 border border-gray-200 rounded-lg shadow-md">
              <p className="text-lg font-medium text-gray-800">
                <strong>Name:</strong> {identity.name}
              </p>
              <p className="text-lg font-medium text-gray-800">
                <strong>ID:</strong> {identity.id}
              </p>
              <p className="text-lg font-medium text-gray-800">
                <strong>Blockchain Proof:</strong> 
                <span className="text-blue-600 font-mono"> {identity.blockchainProof}</span>
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default DigitalIdentityApp;
