const Navbar = ({ walletAddress, onConnectWallet, onInsertSmallPaper }) => {
  return (
    <div className="bg-transparent text-white w-full z-20 flex justify-between items-center px-4 py-2 sm:px-6 sm:py-4 text-sm sm:text-base">
      {/* Left Side - Logo */}
      <div className="text-lg sm:text-xl font-bold">My Digital Identity</div>

      {/* Right Side - Links and Wallet */}
      <div className="flex items-center space-x-3 sm:space-x-6">
        {/* Data Link */}
        <a href="#" className="text-white hover:opacity-80 transition">
          SmallPaper
        </a>

        {/* Keplr Wallet Button */}
        <div className="flex flex-col items-end">  
          <button
            onClick={onConnectWallet}
            className="flex items-center border border-black bg-white text-black text-xs sm:text-lg hover:bg-[#0000FF] hover:text-white hover:border-white transition group"
          >
            <span className="border border-black px-1 py-0.5 sm:px-2 sm:py-1 rounded group-hover:border-white group-hover:text-white">
              K
            </span>
            {walletAddress ? "Wallet Connected" : "CONNECT KEPLR WALLET →"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
