import React, { useState } from "react";
import { Button, Card, CardContent, Input } from "@/components/ui";

const DigitalIdentityApp = () => {
  const [identity, setIdentity] = useState(null);
  const [userData, setUserData] = useState({ name: "", id: "" });

  const handleGenerateIdentity = async () => {
    // Simulating identity creation using Nillion Network
    const generatedIdentity = {
      name: userData.name,
      id: userData.id,
      blockchainProof: "0x123abc456def", // Placeholder for blockchain verification
    };
    setIdentity(generatedIdentity);
  };

  return (
    <div className="flex flex-col items-center p-6">
      <Card className="w-96 p-4">
        <CardContent>
          <h2 className="text-xl font-bold mb-4">Blockchain Digital Identity</h2>
          <Input
            placeholder="Enter Name"
            value={userData.name}
            onChange={(e) => setUserData({ ...userData, name: e.target.value })}
            className="mb-2"
          />
          <Input
            placeholder="Enter ID Number"
            value={userData.id}
            onChange={(e) => setUserData({ ...userData, id: e.target.value })}
            className="mb-2"
          />
          <Button onClick={handleGenerateIdentity} className="w-full">
            Generate Identity
          </Button>
          {identity && (
            <div className="mt-4 p-2 border rounded">
              <p><strong>Name:</strong> {identity.name}</p>
              <p><strong>ID:</strong> {identity.id}</p>
              <p><strong>Blockchain Proof:</strong> {identity.blockchainProof}</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default DigitalIdentityApp;
