import React, { useState, useEffect } from "react";
import Button from "./components/ui/Button";
import Card from "./components/ui/Card";
import CardContent from "./components/ui/CardContent";
import Input from "./components/ui/Input";
import Navbar from "./components/ui/NavBar";
import DrivingLicenseCard from "./components/templates/DrivingLicenseCard";

const DigitalIdentityApp = () => {
  const [walletAddress, setWalletAddress] = useState(null);
  const [userData, setUserData] = useState({
    name: "",
    surname: "",
    fathersName: "",
    mothersName: "",
    dateOfBirth: "",
    placeOfBirth: "",
    issuanceOffice: "",
    idNumber: "",
    datePublish: "",
  });
  const [identity, setIdentity] = useState(null);
  const [showWarning, setShowWarning] = useState(false);
  const [uploadedImage, setUploadedImage] = useState(null);

  useEffect(() => {
    setWalletAddress(null);
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const allowedTypes = ["image/png", "image/jpeg"];
      if (allowedTypes.includes(file.type)) {
        setUploadedImage(file);
      } else {
        alert("Invalid file type. Please upload a PNG or JPEG image.");
      }
    }
  };

  const handleGenerateIdentity = () => {
    const isAnyFieldFilled = Object.values(userData).some((value) => value.trim() !== "");

    if (!isAnyFieldFilled) {
      setShowWarning(true);
      setTimeout(() => setShowWarning(false), 3000);
      return;
    }

    setIdentity({
      ...userData,
      blockchainProof: "0x123abc456def",
      uploadedImage: uploadedImage ? URL.createObjectURL(uploadedImage) : null,
    });
  };

  const isUserDataFilled = Object.values(userData).some((value) => value.trim() !== "");

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      <Navbar walletAddress={walletAddress} />

      <div className="flex flex-col items-center justify-center flex-grow p-6">
        <Card className="w-full max-w-5xl p-8 bg-white rounded-3xl shadow-2xl flex flex-col items-center">
          <CardContent>
            {!isUserDataFilled ? (
              // If user data is empty, show the upload card
              <>
                <h2 className="text-2xl font-bold text-center text-gray-900 mb-4">Upload Identity Image</h2>
                <label htmlFor="file-upload" className="cursor-pointer">
                  <div className="bg-blue-700 hover:bg-blue-800 text-white font-semibold py-3 px-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300 ease-in-out flex items-center space-x-2">
                    <span>📤 Upload Image</span>
                  </div>
                </label>
                <input id="file-upload" type="file" accept=".png, .jpeg, .jpg" onChange={handleImageUpload} className="hidden" />
                {uploadedImage && (
                  <div className="mt-4">
                    <img src={URL.createObjectURL(uploadedImage)} alt="Uploaded Identity" className="max-w-full h-auto rounded-lg shadow-md" />
                  </div>
                )}
              </>
            ) : (
              // If user data is filled, show the identity input form
              <>
                <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">Blockchain Digital Identity</h2>
                <form className="grid grid-cols-3 gap-6 w-full">
                  {Object.keys(userData).map((key) => (
                    <Input
                      key={key}
                      name={key}
                      placeholder={`Enter ${key.replace(/([A-Z])/g, " $1")}`}
                      value={userData[key]}
                      onChange={handleInputChange}
                      className="w-full border-b-2 border-transparent focus:border-blue-600 bg-transparent py-1"
                    />
                  ))}
                </form>

                {showWarning && (
                  <div className="mt-4 p-3 bg-yellow-100 border border-yellow-400 text-yellow-700 rounded-lg">
                    Please fill in at least one field before generating the identity.
                  </div>
                )}

                <Button
                  type="button"
                  onClick={handleGenerateIdentity}
                  className="mt-6 w-full bg-blue-700 text-white py-3 rounded-lg hover:bg-[#4159F6] transition transform hover:scale-105"
                >
                  Generate Identity
                </Button>

                {identity && (
                  <div className="mt-6 p-6 bg-gray-100 border border-gray-200 rounded-lg w-full">
                    {Object.keys(identity).map(
                      (key) =>
                        key !== "uploadedImage" && (
                          <p key={key} className="text-lg font-medium text-gray-800">
                            <strong>{key.replace(/([A-Z])/g, " $1")}:</strong> {identity[key]}
                          </p>
                        )
                    )}
                    {identity.uploadedImage && (
                      <div className="mt-4">
                        <img src={identity.uploadedImage} alt="Uploaded Identity" className="max-w-full h-auto rounded-lg shadow-md" />
                      </div>
                    )}
                  </div>
                )}
              </>
            )}
          </CardContent>
        </Card>
      </div>

      <div className="bg-gray-800 text-white text-center py-4">
        <p>© 2025 All Rights Reserved</p>
      </div>
    </div>
  );
};

export default DigitalIdentityApp;
