import Button from "./Button";

const Navbar = ({ walletAddress, onConnectWallet, onInsertSmallPaper, handleClickPlus, handleInsertLicense }) => {
  return (
    <div className="bg-gray-800 text-white w-full py-4 px-6 fixed top-0 left-0 z-20">
      <div className="flex justify-between items-center">
        {/* Left Side (could add logo or branding here) */}
        <div className="text-xl font-bold">Digital Identity</div>

        {/* "+" Icon and Connect Wallet Button */}
        <div className="flex items-center space-x-6">
            <div className="bg-gray-700 text-white py-2 px-4 rounded-lg shadow-md hover:bg-blue-600 transition" onClick={handleClickPlus}>
                  +
              </div>


          {isDropdownOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200">
              <div className="p-3 hover:bg-gray-100 cursor-pointer" onClick={handleInsertLicense}>
                Insert License
              </div>
            </div>
          )}
        </div>

        {/* Right Side - Connect Wallet and SmallPaper */}
        <div className="flex items-center space-x-6">
          {/* SmallPaper Button or Text */}
          <button
            onClick={onInsertSmallPaper}
            className="text-white hover:text-gray-300 transition"
          >
            SmallPaper
          </button>

          {/* Connect Wallet Button */}
          <Button
            type="button"
            onClick={onConnectWallet}
            className="bg-gray-700 text-white py-2 px-4 rounded-lg shadow-md hover:bg-blue-600 transition"
          >
            {walletAddress ? "Wallet Connected" : "Connect Wallet"}
          </Button>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
