import { useState } from "react";

const Navbar = ({ walletAddress, onConnectWallet, onInsertSmallPaper }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="bg-transparent text-white w-full fixed top-0 left-0 z-20 flex justify-between items-center p-4">
      {/* Left Side - Logo */}
      <div className="text-xl font-bold">My Digital Identity</div>

      {/* Burger Menu Icon for Small Devices */}
      <button
        onClick={toggleMenu}
        className="sm:hidden text-white focus:outline-none"
      >
        <svg
          className="w-6 h-6"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="M4 6h16M4 12h16m-7 6h7"
          ></path>
        </svg>
      </button>

      {/* Right Side - Links and Wallet (Hidden on Small Devices) */}
      <div
        className={`${
          isMenuOpen ? "block" : "hidden"
        } sm:flex sm:items-center sm:space-x-6 absolute sm:static top-16 left-0 w-full sm:w-auto bg-black sm:bg-transparent p-4 sm:p-0`}
      >
        {/* Data Link */}
        <a
          href="#"
          className="block sm:inline text-white hover:opacity-80 transition mb-4 sm:mb-0"
        >
          SmallPaper
        </a>

        {/* Keplr Wallet Button with Icon */}
        <button
          onClick={onConnectWallet}
          className="flex items-center border border-black bg-white text-black px-4 py-2 h-full hover:bg-[#0000FF] hover:text-white hover:border-white transition text-lg group sm:px-6 sm:py-3 w-full sm:w-auto"
        >
          <span className="border border-black px-2 py-1 mr-2 rounded group-hover:border-white group-hover:text-white">
            K
          </span>
          {walletAddress ? "Wallet Connected" : "CONNECT KEPLR WALLET →"}
        </button>
      </div>
    </div>
  );
};

export default Navbar;
