import Button from "./Button";

const Navbar = ({ walletAddress, onConnectWallet, onInsertSmallPaper }) => {
  return (
    <div className="bg-transparent text-white w-full py-4 px-6 fixed top-0 left-0 z-20">
      <div className="flex justify-between items-center">
        {/* Left Side - Logo */}
        <div className="text-xl font-bold">nillion</div>

        {/* Right Side - Links and Wallet */}
        <div className="flex items-center space-x-6">
          {/* Data Link */}
          <a href="#" className="text-white hover:opacity-80 transition">
            DATA →
          </a>

          {/* Keplr Wallet Button with Icon */}
          <button
            onClick={onConnectWallet}
            className="flex items-center border border-black text-black px-4 py-2 rounded-lg hover:bg-gray-200 transition"
          >
            <span className="border border-black px-2 py-1 mr-2 rounded">
              K
            </span>
            {walletAddress ? "Wallet Connected" : "CONNECT KEPLR WALLET →"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
