import React, { useState } from "react";

// Assuming you have custom UI components like Card, Input, and Button.
import { Button, Card, CardContent, Input } from "@/components/ui";

const DigitalIdentityApp = () => {
  const [identity, setIdentity] = useState(null);
  const [userData, setUserData] = useState({ name: "", id: "" });

  const handleGenerateIdentity = async () => {
    // Simulating identity creation using Nillion Network
    const generatedIdentity = {
      name: userData.name,
      id: userData.id,
      blockchainProof: "0x123abc456def", // Placeholder for blockchain verification
    };
    setIdentity(generatedIdentity);
  };

  return (
    <div className="flex flex-col items-center p-6 bg-gray-100 min-h-screen">
      <Card className="w-full max-w-md p-6 bg-white rounded-lg shadow-lg">
        <CardContent>
          <h2 className="text-2xl font-semibold mb-4 text-center text-gray-800">Blockchain Digital Identity</h2>
          <Input
            placeholder="Enter Name"
            value={userData.name}
            onChange={(e) => setUserData({ ...userData, name: e.target.value })}
            className="mb-4 p-2 border border-gray-300 rounded-md w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <Input
            placeholder="Enter ID Number"
            value={userData.id}
            onChange={(e) => setUserData({ ...userData, id: e.target.value })}
            className="mb-4 p-2 border border-gray-300 rounded-md w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <Button
            onClick={handleGenerateIdentity}
            className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition duration-300"
          >
            Generate Identity
          </Button>
          {identity && (
            <div className="mt-6 p-4 bg-white border rounded-md shadow-sm">
              <p className="text-lg font-medium text-gray-700">
                <strong>Name:</strong> {identity.name}
              </p>
              <p className="text-lg font-medium text-gray-700">
                <strong>ID:</strong> {identity.id}
              </p>
              <p className="text-lg font-medium text-gray-700">
                <strong>Blockchain Proof:</strong> {identity.blockchainProof}
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default DigitalIdentityApp;
