import React, { useState, useEffect } from "react";
import Button from "./components/ui/Button";
import Card from "./components/ui/Card";
import CardContent from "./components/ui/CardContent";
import Input from "./components/ui/Input";
import Navbar from "./components/ui/NavBar";
import DrivingLicenseCard from "./components/templates/DrivingLicenseCard";

const DigitalIdentityApp = () => {
  const [identity, setIdentity] = useState(null);
  const [walletAddress, setWalletAddress] = useState(null);
  const [userData, setUserData] = useState({
    name: "",
    surname: "",
    fathersName: "",
    mothersName: "",
    dateOfBirth: "",
    placeOfBirth: "",
    issuanceOffice: "",
    idNumber: "",
    datePublish: "",
  });
  const [uploadedImage, setUploadedImage] = useState(null);

  useEffect(() => {
    setWalletAddress(null);
  }, []);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const allowedTypes = ["image/png", "image/jpeg"];
      if (allowedTypes.includes(file.type)) {
        setUploadedImage(file);
        console.log("Image uploaded:", file.name);
      } else {
        alert("Invalid file type. Please upload a PNG or JPEG image.");
      }
    }
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      <Navbar walletAddress={walletAddress} />

      {!Object.values(userData).some((value) => value !== "") ? (
        <div className="flex flex-col items-center justify-center flex-grow p-6">
          <label className="block text-lg font-medium text-gray-700 mb-4">
            Upload Your Identity Image
          </label>
          <input
            type="file"
            accept=".png, .jpeg, .jpg"
            onChange={handleImageUpload}
            className="block w-64 text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50 focus:outline-none p-3 shadow-lg hover:shadow-xl transition duration-300 ease-in-out"
          />
        </div>
      ) : (
        <div className="flex flex-col items-center justify-center flex-grow p-6">
          <Card className="w-full max-w-5xl p-8 bg-white rounded-3xl shadow-2xl flex flex-col items-center">
            <CardContent>
              <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">
                Blockchain Digital Identity
              </h2>
              <form className="grid grid-cols-3 gap-6 w-full">
                <Input name="idNumber" placeholder="Enter ID Number" value={userData.idNumber} onChange={(e) => setUserData({...userData, idNumber: e.target.value})} />
                <Input name="name" placeholder="Enter Name" value={userData.name} onChange={(e) => setUserData({...userData, name: e.target.value})} />
                <Input name="surname" placeholder="Enter Surname" value={userData.surname} onChange={(e) => setUserData({...userData, surname: e.target.value})} />
              </form>
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
};

export default DigitalIdentityApp;
