import React, { useState } from "react";
import Button from "./components/ui/Button";
import Card from "./components/ui/Card";
import CardContent from "./components/ui/CardContent";
import Input from "./components/ui/Input";
import DrivingLicenseCard from "./components/templates/DrivingLicenseCard"; // Import the component

const DigitalIdentityApp = () => {
  const [identity, setIdentity] = useState(null);
  const [userData, setUserData] = useState({
    name: "",
    surname: "",
    fathersName: "",
    mothersName: "",
    dateOfBirth: "",
    placeOfBirth: "",
    issuanceOffice: "",
    idNumber: "",
    datePublish: "",
  });
  const [showLicenseCard, setShowLicenseCard] = useState(false); // Show driving license cards
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Control dropdown state

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleGenerateIdentity = () => {
    setIdentity({
      ...userData,
      blockchainProof: "0x123abc456def",
    });
  };

  const handleClickPlus = () => {
    setIsDropdownOpen((prev) => !prev); // Toggle dropdown
  };

  const handleInsertLicense = () => {
    setShowLicenseCard(!showLicenseCard); // Show the license card
    setIsDropdownOpen(false); // Close the dropdown
  };

  const handleGenerateLicense = (licenseData) => {
    console.log("Generated License:", licenseData);
    // You can update the state with the license data if necessary
  };

  const handleConnectWallet = () => {
    console.log("Connect wallet clicked");
    // Add your wallet connection logic here
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-r from-[#0000FF] to-[#FFFFFF] p-6">
      {/* "+" Icon and Connect Wallet Button */}
      <div className="absolute top-16 right-6 z-10 flex space-x-4">
        {/* "+" Icon */}
        <div
          className="text-4xl text-blue-600 cursor-pointer"
          onClick={handleClickPlus}
        >
          +
        </div>

        {/* Connect Wallet Button */}
        <Button
          type="button"
          onClick={handleConnectWallet}
          className="w-36 bg-blue-600 text-white py-2 rounded-lg shadow-md hover:bg-blue-700 transition transform hover:scale-105"
        >
          Connect Wallet
        </Button>

        {/* Dropdown Menu */}
        {isDropdownOpen && (
          <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200">
            <div
              className="p-3 hover:bg-gray-100 cursor-pointer"
              onClick={handleInsertLicense}
            >
              Insert License
            </div>
          </div>
        )}
      </div>

      {/* Main Card */}
      <Card className="w-full max-w-5xl p-8 bg-white rounded-3xl shadow-2xl flex flex-col items-center">
        <CardContent>
          <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">
            Blockchain Digital Identity
          </h2>

          <form className="grid grid-cols-3 gap-6 w-full">
            <Input
              name="idNumber"
              placeholder="Enter ID Number"
              value={userData.idNumber}
              onChange={handleInputChange}
              className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1"
            />
            <Input
              name="name"
              placeholder="Enter Name"
              value={userData.name}
              onChange={handleInputChange}
              className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1"
            />
            <Input
              name="surname"
              placeholder="Enter Surname"
              value={userData.surname}
              onChange={handleInputChange}
              className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1"
            />
            <Input
              name="fathersName"
              placeholder="Enter Father's Name"
              value={userData.fathersName}
              onChange={handleInputChange}
              className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1"
            />
            <Input
              name="mothersName"
              placeholder="Enter Mother's Name"
              value={userData.mothersName}
              onChange={handleInputChange}
              className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1"
            />
            <Input
              name="dateOfBirth"
              type="date"
              placeholder="Enter Date of Birth"
              value={userData.dateOfBirth}
              onChange={handleInputChange}
              className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1"
            />
            <Input
              name="placeOfBirth"
              placeholder="Enter Place of Birth"
              value={userData.placeOfBirth}
              onChange={handleInputChange}
              className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1"
            />
            <Input
              name="issuanceOffice"
              placeholder="Enter Issuance Office"
              value={userData.issuanceOffice}
              onChange={handleInputChange}
              className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1"
            />
            <Input
              name="datePublish"
              type="date"
              placeholder="Enter Date of Publish"
              value={userData.datePublish}
              onChange={handleInputChange}
              className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1"
            />
          </form>
          <Button
            type="button"
            onClick={handleGenerateIdentity}
            className="mt-6 w-full bg-blue-600 text-white py-3 rounded-lg shadow-md hover:bg-blue-700 transition transform hover:scale-105"
          >
            Generate Identity
          </Button>

          {identity && (
            <div className="mt-6 p-6 bg-gray-100 border border-gray-200 rounded-lg shadow-md w-full grid grid-cols-3 gap-4 text-left">
              <p className="text-lg font-medium text-gray-800">
                <strong>ID Number:</strong> {identity.idNumber}
              </p>
              <p className="text-lg font-medium text-gray-800">
                <strong>Name:</strong> {identity.name}
              </p>
              <p className="text-lg font-medium text-gray-800">
                <strong>Surname:</strong> {identity.surname}
              </p>
              <p className="text-lg font-medium text-gray-800">
                <strong>Father's Name:</strong> {identity.fathersName}
              </p>
              <p className="text-lg font-medium text-gray-800">
                <strong>Mother's Name:</strong> {identity.mothersName}
              </p>
              <p className="text-lg font-medium text-gray-800">
                <strong>Date of Birth:</strong> {identity.dateOfBirth}
              </p>
              <p className="text-lg font-medium text-gray-800">
                <strong>Place of Birth:</strong> {identity.placeOfBirth}
              </p>
              <p className="text-lg font-medium text-gray-800">
                <strong>Issuance Office:</strong> {identity.issuanceOffice}
              </p>
              <p className="text-lg font-medium text-gray-800">
                <strong>Date of Publish:</strong> {identity.datePublish}
              </p>
              <p className="text-lg font-medium text-gray-800 col-span-3">
                <strong>Blockchain Proof:</strong>{" "}
                <span className="text-green-600 font-mono">
                  {identity.blockchainProof}
                </span>
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Conditionally render Driving License Card below the main card */}
      {showLicenseCard && (
        <div className="mt-8 w-full max-w-5xl">
          <DrivingLicenseCard onGenerateLicense={handleGenerateLicense} />
        </div>
      )}
    </div>
  );
};

export default DigitalIdentityApp;
