import { useRef, useEffect, useState } from 'react';

const Navbar = ({ walletAddress, onConnectWallet, onInsertSmallPaper }) => {
  const navbarRef = useRef(null);
  const [navbarHeight, setNavbarHeight] = useState(0);

  useEffect(() => {
    if (navbarRef.current) {
      setNavbarHeight(navbarRef.current.clientHeight);
    }
  }, []); // Empty dependency array ensures this runs only once after the initial render

  console.log("Navbar Height:", navbarHeight); // Now you have the height

  return (
    <div 
      className="bg-transparent text-white w-full py-4 px-6 fixed top-0 left-0 z-20 flex justify-between items-center"
      ref={navbarRef} // Attach the ref here
    >
      {/* ... your Navbar content ... */}
    </div>
  );
};

export default Navbar;
