import React, { useState } from "react";
import Button from "./components/ui/Button";
import Card from "./components/ui/Card";
import CardContent from "./components/ui/CardContent";
import Input from "./components/ui/Input";
import DrivingLicenseCard from "./components/templates/DrivingLicenseCard"; // Ensure this is the correct path

const DigitalIdentityApp = () => {
  const [identity, setIdentity] = useState(null);
  const [userData, setUserData] = useState({
    name: "",
    surname: "",
    fathersName: "",
    mothersName: "",
    dateOfBirth: "",
    placeOfBirth: "",
    issuanceOffice: "",
    idNumber: "",
    datePublish: "",
  });

  const [showDrivingLicense, setShowDrivingLicense] = useState(false); // New state for showing driving license

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  const handleGenerateIdentity = () => {
    setIdentity({
      ...userData,
      blockchainProof: "0x123abc456def",
    });
  };

  const handleToggleDrivingLicense = () => {
    setShowDrivingLicense(!showDrivingLicense); // Toggle driving license visibility
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-[#0000FF] to-[#FFFFFF] p-6">
      {/* "+" Icon */}
      <div
        className="absolute top-4 right-4 text-4xl text-blue-600 cursor-pointer"
        onClick={handleToggleDrivingLicense} // Toggle on click
      >
        +
      </div>
      <Card className="w-full max-w-5xl p-8 bg-white rounded-3xl shadow-2xl flex flex-col items-center relative">
        <CardContent>
          <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">
            Blockchain Digital Identity
          </h2>

          <form className="grid grid-cols-3 gap-6 w-full">
            {/* Input fields for identity data */}
            <Input
              name="idNumber"
              placeholder="Enter ID Number"
              value={userData.idNumber}
              onChange={handleInputChange}
              className="w-full border-b-2 border-transparent focus:border-blue-600 focus:outline-none bg-transparent py-1"
            />
            {/* Other input fields here */}
          </form>

          <Button
            type="button"
            onClick={handleGenerateIdentity}
            className="mt-6 w-full bg-blue-600 text-white py-3 rounded-lg shadow-md hover:bg-blue-700 transition transform hover:scale-105"
          >
            Generate Identity
          </Button>

          {/* Display identity information */}
          {identity && (
            <div className="mt-6 p-6 bg-gray-100 border border-gray-200 rounded-lg shadow-md w-full grid grid-cols-3 gap-4 text-left">
              <p className="text-lg font-medium text-gray-800">
                <strong>ID Number:</strong> {identity.idNumber}
              </p>
              {/* Other identity fields here */}
              <p className="text-lg font-medium text-gray-800 col-span-3">
                <strong>Blockchain Proof:</strong>
                <span className="text-green-600 font-mono">
                  {identity.blockchainProof}
                </span>
              </p>
            </div>
          )}

          {/* Show driving license card if toggled */}
          {showDrivingLicense && (
            <div className="mt-6 w-full">
              <DrivingLicenseCard />
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default DigitalIdentityApp;
